function log(strMessage) {
	date = new Date();
	if (strPrefix !== undefined) {
		console.log('[' + date.toLocaleTimeString() + '] [' + strPrefix + '] '
				+ strMessage);
	} else {
		console.log('[' + date.toLocaleTimeString() + '] ' + strMessage);
	}
};


function execCli(strCommandLine, boolInOut) {
	var execSync = require('execSync');
	boolInOut = boolInOut || false;
	if (boolInOut === true) {
		strCommandLine = strCommandLine + " 2>&1";
	}
	strOutput = '';
	strOutput = execSync.exec(strCommandLine);
	return strOutput.stdout;
}

module.exports = {
	execCli : execCli,
	log : log,
};
