/**
 * New node file
 */

function run(strInputFile, strOutputFile, objTask) {
	switch (objTask.tool) {
	case 'mp4box':
		var pack = require('./Mp4Box');
		pack.run(strInputFile, strOutputFile);
		break;
	default:
		throw new Error('Packager method not available');
	}

}

module.exports = {
	run : run
};
