/**
 * New node file
 */

function run(strInputPath, objTask) {
	switch (objTask.tool) {
	case 'lftp':
		var transfert = require('./Lftp');
		if (objTask.settings !== undefined) {
			transfert.setSettings(objTask.settings);
		}
		transfert.run(strInputPath);
		break;
	default:
		throw new Error('Packager method not available');
	}

}

module.exports = {
	run : run
};
